import './App.css';
import { useState, useEffect } from 'react';
import Compteur from './components/Compteur';
import Bouton from './components/Bouton';
import Avatar from '@mui/material/Avatar';
import ButtonAppBar from './AppBar';
import NewEmail from './components/NewEmail';
import Case from './components/Case';
import Info from './components/Info';
import Liste from "./components/Liste";
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
//import AppBarWithDrawer from './components/AppBarWithDrawer';
import CreerRecette from './pages/CreerRecette';
import SupprimeRecette from './pages/SupprimeRecette';
import ModifierRecette from './pages/ModifierRecette';
import Box from '@mui/material/Box';
import Note from './components/Note';


function App() {
  const [count, setCount] = useState(0);
  const [message, setMessage] = useState('');

  const incrementCount = () => {
    setCount((prevCount) => {
      const newCount = prevCount + 1;
      return newCount % 10 === 0 ? 0 : newCount;
    });
  }

  useEffect(() => {
    if (count % 10 === 0 && count !== 0) {
      setMessage("C'est un multiple de 10 !");
    } else if (count % 5 === 0 && count !== 0) {
      setMessage("C'est un multiple de 5 !");
    } else {
      setMessage('');
    }
  }, [count]);

  return (
    <div className="App">
      <ButtonAppBar />
      {/* <Compteur count={count} /> */}
      <Bouton onIncrement={incrementCount} />

      {message && <p>{message}</p>}
      <Box
  display="flex"

  alignItems="center"
  minHeight="20vh"
>
      <Avatar alt="Stark"
        src="https://th.bing.com/th/id/OIP.30tg6nnr5000rj6LoJqD2wHaEo?rs=1&pid=ImgDetMain"
        sx={{ width: 100, height: 100 }}
      />
      </Box>
      <NewEmail></NewEmail>
      <Case></Case>
      <Info></Info>
      <Liste></Liste>
      <Note></Note>
    </div>
  );
}
