import './App.css';
import { useState, useEffect } from 'react';
import './components/Compteur';
import Bouton from './component/Bouton';
import Avatar from '@mui/material/Avatar';

  function App() {
    const [count, setCount] = useState(0);
    const [message, setMessage] = useState('');

    const incrementCount = () => {
      setCount((prevCount) => {
        const newCount = prevCount + 1;
        return newCount % 10 === 0 ? 0 : newCount;
      });
    }

    useEffect(() => {
      if (count % 10 === 0 && count !== 0) {
        setMessage("C'est un multiple de 10 !");
      } else if (count % 5 === 0 && count !== 0) {
        setMessage("C'est un multiple de 5 !");
      } else {
        setMessage('');
      }
    }, [count]);

    return (
      <div className="App">
        {/* <Compteur count={count} /> */}
        <Bouton onIncrement={incrementCount} />
        {message && <p>{message}</p>}
        <Avatar alt="Bruce Wayne"
 src="https://th.bing.com/th/id/R.1a0777161ad6b235b35d162191fe7142?rik=4XeYl0uOZMYmaw&riu=http%3a%2f%2fprop-replica.weebly.com%2fuploads%2f1%2f2%2f5%2f7%2f12574326%2f161679441.jpg&ehk=VbY7G68Toyzp%2b8VNgV5wrRvAj6HIN6Gyw1mIZjLauzg%3d&risl=&pid=ImgRaw&r=0"
 sx={{ width: 56, height: 56 }}></Avatar>

      </div>

    );  
  }
export default App;
