import * as React from 'react';
import { 
  AppBar, 
  Toolbar, 
  Typography, 
  IconButton,
  Button, 
  FormControl, 
  FormControlLabel, 
  Checkbox, 
  InputLabel, 
  OutlinedInput, 
  TextField, 
  InputAdornment, 
  Link, 

} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import AccountCircle from '@mui/icons-material/AccountCircle';
import Visibility from '@mui/icons-material/Visibility';
import VisibilityOff from '@mui/icons-material/VisibilityOff';
import MenuIcon from '@mui/icons-material/Menu';
import { AppProvider } from '@toolpad/core/AppProvider';
import { SignInPage } from '@toolpad/core/SignInPage';
import { useTheme } from '@mui/material/styles';

const providers = [{ id: 'credentials', name: 'Email and Password' }];

function CustomEmailField() {
  return (
    <TextField
      id="input-with-icon-textfield"
      label="Email"
      name="email"
      type="email"
      size="small"
      required
      fullWidth
      InputProps={{
        startAdornment: (
          <InputAdornment position="start">
            <AccountCircle fontSize="inherit" />
          </InputAdornment>
        ),
      }}
      variant="outlined"
    />
  );
}

function CustomPasswordField() {
  const [showPassword, setShowPassword] = React.useState(false);

  const handleClickShowPassword = () => setShowPassword((show) => !show);

  const handleMouseDownPassword = (event) => {
    event.preventDefault();
  };

  return (
    <FormControl sx={{ my: 2 }} fullWidth variant="outlined">
      <InputLabel size="small" htmlFor="outlined-adornment-password">
        Mot de Passe
      </InputLabel>
      <OutlinedInput
        id="outlined-adornment-password"
        type={showPassword ? 'text' : 'password'}
        name="password"
        size="small"
        endAdornment={
          <InputAdornment position="end">
            <IconButton
              aria-label="toggle password visibility"
              onClick={handleClickShowPassword}
              onMouseDown={handleMouseDownPassword}
              edge="end"
              size="small"
            >
              {showPassword ? <VisibilityOff fontSize="inherit" /> : <Visibility fontSize="inherit" />}
            </IconButton>
          </InputAdornment>
        }
        label="Password"
      />
    </FormControl>
  );
}

function CustomButton() {
  return (
    <Button
      type="submit"
      variant="outlined"
      color="info"
      size="small"
      disableElevation
      fullWidth
      sx={{ my: 2 }}
    >
     S'inscrire
    </Button>
  );
}

function SignUpLink() {
  return (
    <Link href="/Utilisateurs" variant="body2">
 Connectez-vous
    </Link>
  );
}

function Title() {
  return <h2 style={{ marginBottom: 8 }}>Inscription</h2>;
}


function AgreeWithTerms() {
  return (
    <FormControlLabel
      control={
        <Checkbox
          name="tandc"
          value="true"
          color="primary"
          sx={{ padding: 0.5, '& .MuiSvgIcon-root': { fontSize: 20 } }}
        />
      }
      slotProps={{
        typography: {
          fontSize: 14,
        },
      }}
      color="textSecondary"
      label="Je suis d'accord avec la politique confidentialité"
    />
  );
}

export default function SlotsSignIn() {
  const theme = useTheme();
  const [drawerOpen, setDrawerOpen] = React.useState(false);
  const navigate = useNavigate();

  return (
    <AppProvider theme={theme}>
      <AppBar position="static" color="primary">
        <Toolbar>
          <IconButton
            edge="start"
            sx={{ mr: 2 }}
            color="inherit"
            aria-label="menu"
            onClick={() => setDrawerOpen(true)}
          >
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" color="inherit" component="div">
           Inscrivez-vous
          </Typography>
        </Toolbar>
      </AppBar>
      <SignInPage
  signIn={(provider, formData) => {
    const email = formData.get('email');
    const password = formData.get('password');
    const tandc = formData.get('tandc');

   
    if (email === 'luchino@gmail.com' && password === '6Alamaison' && tandc === 'true') {

      navigate('/Accueil');
    } else {
      
      alert('Identifiants incorrects ou conditions non acceptées.');
    }
  }}
        slots={{
          title: Title,

          emailField: CustomEmailField,
          passwordField: CustomPasswordField,
          submitButton: CustomButton,
          signUpLink: SignUpLink,
          rememberMe: AgreeWithTerms,
        }}
        providers={providers}
      />
    </AppProvider>
  );
}
