import React from 'react';
import { useNavigate } from 'react-router-dom';
import { AppProvider } from '@toolpad/core/AppProvider';
import { SignInPage } from '@toolpad/core/SignInPage';
import { useTheme } from '@mui/material/styles';
import Box from '@mui/material/Box';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import InboxIcon from '@mui/icons-material/MoveToInbox';
import MailIcon from '@mui/icons-material/Mail';

const providers = [{ id: 'credentials', name: 'Email and Password' }];

const signIn = async (provider, formData) => {
  try {
    // Simuler une requête d'authentification
    await new Promise((resolve) => setTimeout(resolve, 300));
    console.log(`Signing in with "${provider.name}" and credentials: ${formData.get('email')}, ${formData.get('password')}`);
    return { success: true, user: { email: formData.get('email') } };
  } catch (error) {
    console.error('Erreur de connexion:', error);
    return { success: false, error: 'Échec de la connexion' };
  }
};

export default function CredentialsSignInPage() {
  const theme = useTheme();
  const navigate = useNavigate();

  const handleNavigation = (path) => {
    navigate(path);
  };

  const DrawerList = (
    <Box sx={{ width: 250 }} role="presentation">
      <List>
        {[
          { text: 'Accueil', path: '/' },
          { text: 'Creer votre recette', path: '/CreerRecette' },
          { text: 'Partager votre recette', path: '/PartagerRecette' },
          { text: 'Modifier votre recette', path: '/ModifierRecette' },
        ].map((item, index) => (
          <ListItem key={item.text} disablePadding>
            <ListItemButton onClick={() => handleNavigation(item.path)}>
              <ListItemIcon>
                {index % 2 === 0 ? <InboxIcon /> : <MailIcon />}
              </ListItemIcon>
              <ListItemText primary={item.text} />
            </ListItemButton>
          </ListItem>
        ))}
      </List>
    </Box>
  );

  return (
    <AppProvider theme={theme}>
      <SignInPage
        signIn={signIn}
        providers={providers}
        slotProps={{ emailField: { autoFocus: false } }}
      />
      {DrawerList}
    </AppProvider>
  );
}
