import React, { useState, useEffect } from 'react';
import AppBarWithDrawer from './AppBarWithDrawer';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import Card from '@mui/material/Card';
import CardMedia from '@mui/material/CardMedia';
import CardContent from '@mui/material/CardContent';
import SearchBar from '../pages/SearchBar';
import Notation from '../Components/Notation';
import Rating from '@mui/material/Rating';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';

const Home = () => {
  const [meals, setMeals] = useState([]);
  const [searchLetter, setSearchLetter] = useState('a');
  const [ratings, setRatings] = useState({});
  const [comments, setComments] = useState({});

  const fetchMeals = async (letter) => {
    try {
      const response = await fetch(`https://www.themealdb.com/api/json/v1/1/search.php?f=${letter}`);
      const data = await response.json();
      setMeals(data.meals);
    } catch (error) {
      console.error('Erreur lors de la récupération des données:', error);
    }
  };

  useEffect(() => {
    fetchMeals(searchLetter);
  }, [searchLetter]);

  const handleSearch = (letter) => {
    setSearchLetter(letter);
  };

  const handleRatingChange = (mealId, newRating) => {
    setRatings((prevRatings) => ({
      ...prevRatings,
      [mealId]: newRating,
    }));
  };

  const handleCommentChange = (mealId, newComment) => {
    setComments((prevComments) => ({
      ...prevComments,
      [mealId]: newComment,
    }));
  };

  const handleCommentSubmit = (mealId) => {
    // Vous pouvez ajouter une logique pour envoyer le commentaire à un serveur ici
    console.log(`Commentaire pour la recette ${mealId}:`, comments[mealId]);
  };

  return (
    <Box sx={{
      minHeight: '100vh',
      backgroundImage: 'url("https://media.istockphoto.com/id/2158116531/fr/photo/comptoir-de-cuisine-avec-ustensiles-et-espace-de-copie-mur-de-briques.jpg?s=2048x2048&w=is&k=20&c=axFyvtqoLNiCANND4PLWbIo2CuibaswN2tkHgnE1vEU=")',
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      backgroundRepeat: 'no-repeat',
      backgroundAttachment: 'fixed'
    }}>
      <AppBarWithDrawer />
      <Box sx={{ p: 3 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Recettes
        </Typography>
        <SearchBar onSearch={handleSearch} />
        <Grid container spacing={3}>
          {meals && meals.map((meal) => (
            <Grid item xs={12} sm={6} md={4} key={meal.idMeal}>
              <Card>
                <CardMedia
                  component="img"
                  height="140"
                  image={meal.strMealThumb}
                  alt={meal.strMeal}
                />
                <CardContent>
                  <Typography variant="h6" component="div">
                    {meal.strMeal}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    {meal.strInstructions.substring(0, 100)}...
                  </Typography>
                  <Rating
                    name={`rating-${meal.idMeal}`}
                    value={ratings[meal.idMeal] || 0}
                    onChange={(event, newValue) => handleRatingChange(meal.idMeal, newValue)}
                  />
                  <TextField
                    fullWidth
                    label="Commentaire"
                    value={comments[meal.idMeal] || ''}
                    onChange={(event) => handleCommentChange(meal.idMeal, event.target.value)}
                    multiline
                    rows={2}
                    variant="outlined"
                    sx={{ mt: 2 }}
                  />
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={() => handleCommentSubmit(meal.idMeal)}
                    sx={{ mt: 1 }}
                  >
                    Soumettre
                  </Button>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Box>
    </Box>
  );
};

export default Home;
