import * as React from 'react';
import Alert from '@mui/material/Alert';
import AlertTitle from '@mui/material/AlertTitle';
import Stack from '@mui/material/Stack';
import Box from '@mui/material/Box';

export default function Info() {
    const dateModification = new Date().toLocaleDateString('fr-FR', {
        day: 'numeric', 
        month: 'long', 
        year: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
    });

    return (
        <Box display="flex" justifyContent="center" width="100%">
          <Stack sx={{ width: '80%', maxWidth: '600px' }} spacing={2}>
            <Alert severity="info">
              <AlertTitle>Modification de la recette</AlertTitle>
              Votre recette a été modifiée le {dateModification}
            </Alert>
            <Alert severity="success">Votre recette a été créée avec succès.</Alert>
            <Alert severity="warning">Votre recette n'a pas été sauvegardée.</Alert>
            <Alert severity="error">Votre recette contient une erreur.</Alert>
          </Stack>
        </Box>
    );
}
