import './App.css';

import { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Avatar from '@mui/material/Avatar';
import Box from '@mui/material/Box';
import ButtonAppBar from './AppBar';
import Liste from './components/Liste';
import Note from './components/Note';
import CreerRecette from './pages/CreerRecette';
import SupprimeRecette from './pages/SupprimeRecette';
import ModifierRecette from './pages/ModifierRecette';
import CreerUtilisateur from './pages/CreerUtilisateur';

import Utilisateurs from './pages/Utilisateurs';
import { nativeSelectClasses } from '@mui/material';

function App() {
  const [count, setCount] = useState(0);
  const [message, setMessage] = useState('');

  const incrementCount = () => {
    setCount((prevCount) => {
      const newCount = prevCount + 1;
      return newCount % 10 === 0 ? 0 : newCount;
    });
  }

  useEffect(() => {
    if (count % 10 === 0 && count !== 0) {
      setMessage("C'est un multiple de 10 !");
    } else if (count % 5 === 0 && count !== 0) {
      setMessage("C'est un multiple de 5 !");
    } else {
      setMessage('');
    }
  }, [count]);

  return (

      <div className="App">
        <ButtonAppBar />
        <Routes>
          <Route path="/" element={
            <>

              {message && <p>{message}</p>}
              <Box display="flex" justifyContent="flex-start" alignItems="flex-start" minHeight="20vh" >
                <Avatar alt="Stark" src="https://th.bing.com/th/id/OIP.30tg6nnr5000rj6LoJqD2wHaEo?rs=1&pid=ImgDetMain" sx={{ width: 100, height: 100 }} />
              </Box>
              <Liste />
              <Note />
            </>
          } />
        <Route path="/CreerUtilisateur" element={<CreerUtilisateur />} />
        <Route path="/Utilisateurs"element={<Utilisateurs/>}/>
          <Route path="/CreerRecette" element={<CreerRecette />} />
          <Route path="/ModifierRecette" element={<ModifierRecette />} />
          <Route path="/SupprimeRecette" element={<SupprimeRecette />} />
        </Routes>
      </div>

  );
}



export default App;
