import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { AppBar, Toolbar, IconButton, Typography, TextField, InputAdornment, FormControl, InputLabel, OutlinedInput, Button, Link, FormControlLabel, Checkbox, Box } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import AccountCircle from '@mui/icons-material/AccountCircle';
import Visibility from '@mui/icons-material/Visibility';
import VisibilityOff from '@mui/icons-material/VisibilityOff';

function Utilisateurs() {
  const navigate = useNavigate();
  const [showPassword, setShowPassword] = useState(false);

  const handleSignIn = (event) => {
    event.preventDefault();
    const formData = new FormData(event.currentTarget);
    const email = formData.get('email');
    const password = formData.get('password');
    const tandc = formData.get('tandc');

    if (email === 'luchino@gmail.com' && password === '6Alamaison' && tandc) {
      navigate('/'); 
    } else {
      alert('Identifiants incorrects ou conditions non acceptées.');
    }
  };

  return (
    <div>
      <AppBar position="static" color="primary">
        <Toolbar>
          <IconButton edge="start" color="inherit" aria-label="menu">
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" color="inherit">
            Authentification
          </Typography>
        </Toolbar>
      </AppBar>
      <Box component="form" onSubmit={handleSignIn} sx={{ mt: 3, mx: 'auto', maxWidth: 400, p: 2, borderRadius: 1, boxShadow: 3 }}>
        <Typography variant="h5" component="h1" gutterBottom>
          Connexion
        </Typography>
        <TextField
          fullWidth
          label="Email"
          name="email"
          type="email"
          required
          InputProps={{
            startAdornment: (
              <InputAdornment position="start">
                <AccountCircle />
              </InputAdornment>
            ),
          }}
        />
        <FormControl sx={{ mt: 2 }} fullWidth variant="outlined">
          <InputLabel htmlFor="outlined-adornment-password">Mot de passe</InputLabel>
          <OutlinedInput
            id="outlined-adornment-password"
            name="password"
            type={showPassword ? 'text' : 'password'}
            endAdornment={
              <InputAdornment position="end">
                <IconButton
                  aria-label="toggle password visibility"
                  onClick={() => setShowPassword(!showPassword)}
                  edge="end"
                >
                  {showPassword ? <VisibilityOff /> : <Visibility />}
                </IconButton>
              </InputAdornment>
            }
            label="Mot de passe"
          />
        </FormControl>
        <FormControlLabel
          control={<Checkbox name="tandc" required />}
          label="J'accepte les termes et conditions"
        />
        <Button type="submit" variant="contained" color="primary" fullWidth sx={{ mt: 2 }}>
          Se connecter
        </Button>
        <Link href="/CreerUtilisateur" variant="body2" sx={{ display: 'block', textAlign: 'center', mt: 2 }}>
          Créer un compte
        </Link>
      </Box>
    </div>
  );
}

export default Utilisateurs;
