import * as React from 'react';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import AppBar from '@mui/material/AppBar';
import CssBaseline from '@mui/material/CssBaseline';
import GlobalStyles from '@mui/material/GlobalStyles';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import Fab from '@mui/material/Fab';
import AddIcon from '@mui/icons-material/Add';
import Snackbar from '@mui/material/Snackbar';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import TextField from '@mui/material/TextField';
import Box from '@mui/material/Box';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import InboxIcon from '@mui/icons-material/MoveToInbox';
import MailIcon from '@mui/icons-material/Mail';
import Drawer from '@mui/material/Drawer';

function RecetteForm({ open, onClose }) {
  return (
    <Dialog open={open} onClose={onClose}>
      <DialogTitle>Créer une nouvelle recette</DialogTitle>
      <DialogContent>
        <Box component="form" sx={{ '& > :not(style)': { m: 1 } }}>
          <TextField fullWidth label="Nom de la recette" variant="outlined" />
          <TextField fullWidth multiline rows={4} label="Ingrédients" variant="outlined" />
          <TextField fullWidth multiline rows={4} label="Étapes de la recette" variant="outlined" />
          <Button variant="contained" component="label">
            Ajouter une photo
            <input type="file" hidden accept="image/*" />
          </Button>
          <Button variant="contained" color="primary" onClick={onClose}>
            Enregistrer la recette
          </Button>
        </Box>
      </DialogContent>
    </Dialog>
  );
}

export default function CreerRecette() {
  const [openForm, setOpenForm] = useState(true);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [snackbarOpen, setSnackbarOpen] = useState(false);
  const navigate = useNavigate();

  const handleOpenForm = () => {
    setOpenForm(true);
  };

  const handleCloseForm = () => {
    setOpenForm(false);
    setSnackbarOpen(true);
  };
  
  const handleNavigation = (path) => {
    navigate(path);
    setDrawerOpen(false);
  };

  const DrawerList = (
    <Box sx={{ width: 250 }} role="presentation">
      <List>
        {[
          { text: 'Accueil', path: '/' },
          { text: 'Modifier votre recette', path: '/ModifierRecette' },
          { text: 'Supprimer votre recette', path: '/SupprimeRecette' },
      
        ].map((item, index) => (
          <ListItem key={item.text} disablePadding>
            <ListItemButton onClick={() => handleNavigation(item.path)}>
              <ListItemIcon>
                {index % 2 === 0 ? <InboxIcon /> : <MailIcon />}
              </ListItemIcon>
              <ListItemText primary={item.text} />
            </ListItemButton> 
          </ListItem>
        ))}
      </List>
    </Box>
  );
  
  return (
    <React.Fragment>
      <CssBaseline />
      <GlobalStyles
        styles={(theme) => ({
          body: { backgroundColor: theme.palette.background.paper },
        })}
      />

      <div>
        <AppBar position="static" color="primary">
          <Toolbar>
            <IconButton
              edge="start"
              sx={{ mr: 2 }}
              color="inherit"
              aria-label="menu"
              onClick={() => setDrawerOpen(true)}
            >
              <MenuIcon />
            </IconButton>
            <Typography  variant="h6" color="inherit" component="div">
             <div>Création de votre recette</div>
            </Typography>
          </Toolbar>
        </AppBar>

        <Drawer
          anchor="left"
          open={drawerOpen}
          onClose={() => setDrawerOpen(false)}
        >
          {DrawerList}
        </Drawer>

        <Fab
          color="secondary"
          sx={(theme) => ({
            position: 'fixed',
            bottom: theme.spacing(2),
            right: theme.spacing(2),
          })}
          onClick={handleOpenForm}
        >
          <AddIcon />
        </Fab>

        <Snackbar
          open={snackbarOpen}
          autoHideDuration={6000}
          onClose={() => setSnackbarOpen(false)}
          message="Enregistrement de la recette"
          action={
            <Button color="inherit" size="small" onClick={() => setSnackbarOpen(false)}>
              Fermer
            </Button>
          }
          sx={{ bottom: { xs: 90, sm: 0 } }}
        />

        <RecetteForm open={openForm} onClose={handleCloseForm} />
      </div>
    </React.Fragment>
  );
}
