import * as React from 'react';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import Drawer from '@mui/material/Drawer';
import List from '@mui/material/List';
import Divider from '@mui/material/Divider';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import InboxIcon from '@mui/icons-material/MoveToInbox';
import MailIcon from '@mui/icons-material/Mail';
import { useNavigate } from 'react-router-dom';
import { Avatar } from '@mui/material';

function AppBarWithDrawer() {
  const [open, setOpen] = React.useState(false);
  const navigate = useNavigate();

  const toggleDrawer = (newOpen) => () => {
    setOpen(newOpen);
  };

  const handleNavigation = (path) => {
    navigate(path);
    setOpen(false);
    console.log('Hello world')
  };

  const handleLogin = () => {
    navigate('/Utilisateurs');
  };

  const DrawerList = (
    <Box sx={{ width: 250 }} role="presentation">
     
      <Divider />
      <List>
        {[
          { text: 'Créer votre recette', path: '/CreerRecette' },
          { text: 'Modifier votre recette', path: '/ModifierRecette' },
          { text: 'Supprimer votre recette', path: '/SupprimeRecette' },
          
        ].map((item, index) => (
          <ListItem key={item.text} disablePadding>
            <ListItemButton onClick={() => handleNavigation(item.path)}>
              <ListItemIcon>
                {index % 2 === 0 ? <InboxIcon /> : <MailIcon />}
              </ListItemIcon>
              <ListItemText primary={item.text} />
            </ListItemButton>
          </ListItem>
        ))}
      </List>
    </Box>
  );
 
  return (
    <Box sx={{ flexGrow: 1 }}>
     <AppBar position="static" sx={{ minHeight: '100px' }}>
  <Toolbar sx={{ display: 'flex', alignItems: 'center' }}> 
    <Avatar 
      alt="Stark" 
      src="https://th.bing.com/th/id/OIP.30tg6nnr5000rj6LoJqD2wHaEo?rs=1&pid=ImgDetMain" 
      sx={{ width: 60, height: 60, marginRight: 2 }} 
    />
    <IconButton
      size="large"
      color="inherit"
      aria-label="menu"
      sx={{ mr: 3, marginLeft: 1 }}
      onClick={toggleDrawer(true)}
    >
      <MenuIcon />
    </IconButton>
    <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
      Votre Recette de Cuisine
    </Typography>
    <Button color="inherit" onClick={handleLogin}>Connectez-vous</Button>
  </Toolbar>
</AppBar>
      <Drawer open={open} onClose={toggleDrawer(false)}>
        {DrawerList}
      </Drawer>
    </Box>
  );
}

export default AppBarWithDrawer;
