import React, { useState } from 'react';

function CreerRecette() {
  const [nom, setNom] = useState('');
  const [ingredients, setIngredients] = useState('');
  const [etapes, setEtapes] = useState('');
  const [photos, setPhotos] = useState([]);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Ici, vous pouvez ajouter la logique pour envoyer la recette à votre backend
    console.log({ nom, ingredients, etapes, photos });
  };

  const handlePhotoUpload = (e) => {
    const files = Array.from(e.target.files);
    setPhotos(files);
  };

  return (
    <div>
      <h1>Créer une nouvelle recette</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="nom">Nom de la recette:</label>
          <input
            type="text"
            id="nom"
            value={nom}
            onChange={(e) => setNom(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="ingredients">Ingrédients:</label>
          <textarea
            id="ingredients"
            value={ingredients}
            onChange={(e) => setIngredients(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="etapes">Étapes de préparation:</label>
          <textarea
            id="etapes"
            value={etapes}
            onChange={(e) => setEtapes(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="photos">Photos:</label>
          <input
            type="file"
            id="photos"
            multiple
            onChange={handlePhotoUpload}
            accept="image/*"
          />
        </div>
        <button type="submit">Créer la recette</button>
      </form>
    </div>
  );
}

export default CreerRecette;
