import * as React from 'react';
import { useState } from 'react';
import AppBar from '@mui/material/AppBar';
import CssBaseline from '@mui/material/CssBaseline';
import GlobalStyles from '@mui/material/GlobalStyles';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import Fab from '@mui/material/Fab';
import AddIcon from '@mui/icons-material/Add';
import Snackbar from '@mui/material/Snackbar';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import TextField from '@mui/material/TextField';
import Box from '@mui/material/Box';

function RecetteForm({ open, onClose }) {
  return (
    <Dialog open={open} onClose={onClose}>
      <DialogTitle>Créer une nouvelle recette</DialogTitle>
      <DialogContent>
        <Box component="form" sx={{ '& > :not(style)': { m: 1 } }}>
          <TextField fullWidth label="Nom de la recette" variant="outlined" />
          <TextField fullWidth multiline rows={4} label="Ingrédients" variant="outlined" />
          <TextField fullWidth multiline rows={4} label="Étapes de la recette" variant="outlined" />
          <Button variant="contained" component="label">
            Ajouter une photo
            <input type="file" hidden accept="image/*" />
          </Button>
          <Button variant="contained" color="primary" onClick={onClose}>
            Enregistrer la recette
          </Button>
        </Box>
      </DialogContent>
    </Dialog>
  );
}

export default function CreerRecette() {
  const [openForm, setOpenForm] = useState(false);

  const handleOpenForm = () => {
    setOpenForm(true);
  };

  const handleCloseForm = () => {
    setOpenForm(false);
  };

  return (
    <React.Fragment>
      <CssBaseline />
      <GlobalStyles
        styles={(theme) => ({
          body: { backgroundColor: theme.palette.background.paper },
        })}
      />
      <div>
        <AppBar position="static" color="primary">
          <Toolbar>
            <IconButton
              edge="start"
              sx={{ mr: 2 }}
              color="inherit"
              aria-label="menu"
            >
              <MenuIcon />
            </IconButton>
            <Typography variant="h6" color="inherit" component="div">
             Création de votre recette
            </Typography>
          </Toolbar>
        </AppBar>
        <Fab
          color="secondary"
          sx={(theme) => ({
            position: 'absolute',
            bottom: theme.spacing(2),
            right: theme.spacing(2),
          })}
          onClick={handleOpenForm}
        >
          <AddIcon />
        </Fab>
        <Snackbar
      
     
          sx={{ bottom: { xs: 90, sm: 0 } }}
        />
        <RecetteForm open={openForm} onClose={handleCloseForm} />
      </div>
    </React.Fragment>
  );
}
