import CounterDisplay from './component/CounterDisplay.jsx'
import IncrementButton from './component/IncrementButton.jsx'
import './App.css';
import { useState } from 'react';

  function App() {
    const [count, setCount] = useState(0);

    const incrementCount = () => {
      setCount((prevCount) => {
        const newCount = prevCount + 1;
        return newCount % 10 === 0 ? 0 : newCount;
      });
    }
    return (
      <div className="App">
        <CounterDisplay count={count} />
        <IncrementButton onIncrement={incrementCount} />
      </div>
    );
  }

export default App;
