import './App.css';
import { useState, useEffect } from 'react';
import Compteur from './components/Compteur';
import Bouton from './components/Bouton';
import Avatar from '@mui/material/Avatar';
import * as React from 'react';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';

function ButtonAppBar() {
  return (
    <Box sx={{ flexGrow: 1 }}>
      <AppBar position="static">
        <Toolbar>
          <IconButton
            size="large"
            edge="start"
            color="inherit"
            aria-label="menu"
            sx={{ mr: 2 }}
          >
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            News
          </Typography>
          <Button color="inherit">Login</Button>
        </Toolbar>
      </AppBar>
    </Box>
  );
}

function App() {
  const [count, setCount] = useState(0);
  const [message, setMessage] = useState('');

  const incrementCount = () => {
    setCount((prevCount) => {
      const newCount = prevCount + 1;
      return newCount % 10 === 0 ? 0 : newCount;
    });
  }

  useEffect(() => {
    if (count % 10 === 0 && count !== 0) {
      setMessage("C'est un multiple de 10 !");
    } else if (count % 5 === 0 && count !== 0) {
      setMessage("C'est un multiple de 5 !");
    } else {
      setMessage('');
    }
  }, [count]);

  return (
    <div className="App">
      <ButtonAppBar />
      {/* <Compteur count={count} /> */}
      <Bouton onIncrement={incrementCount} />
      {message && <p>{message}</p>}
      <Avatar alt="Stark"
        src="https://th.bing.com/th/id/OIP.30tg6nnr5000rj6LoJqD2wHaEo?rs=1&pid=ImgDetMain"
        sx={{ width: 100, height: 100 }}
      />
    </div>
  );
}

export default App;
