import './App.css';
import { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Avatar from '@mui/material/Avatar';
import Box from '@mui/material/Box';
import ButtonAppBar from './AppBar';
import Bouton from './components/Bouton';
import Case from './components/Case';
import Info from './components/Info';
import Liste from './components/Liste';
import Note from './components/Note';
import CreerRecette from './pages/CreerRecette';
import SupprimeRecette from './pages/SupprimeRecette';
import ModifierRecette from './pages/ModifierRecette';
import PartagerRecette from './pages/PartagerRecette';

function App() {
  const [count, setCount] = useState(0);
  const [message, setMessage] = useState('');

  const incrementCount = () => {
    setCount((prevCount) => {
      const newCount = prevCount + 1;
      return newCount % 10 === 0 ? 0 : newCount;
    });
  }

  useEffect(() => {
    if (count % 10 === 0 && count !== 0) {
      setMessage("C'est un multiple de 10 !");
    } else if (count % 5 === 0 && count !== 0) {
      setMessage("C'est un multiple de 5 !");
    } else {
      setMessage('');
    }
  }, [count]);

  return (
    <Router>
      <div className="App">
        <ButtonAppBar />
        <Routes>
          <Route path="/" element={
            <>
              <Bouton onIncrement={incrementCount} />
              {message && <p>{message}</p>}
              <Box display="flex" alignItems="center" minHeight="20vh">
                <Avatar alt="Stark" src="https://th.bing.com/th/id/OIP.30tg6nnr5000rj6LoJqD2wHaEo?rs=1&pid=ImgDetMain" sx={{ width: 100, height: 100 }} />
              </Box>
              <Case />
              <Info />
              <Liste />
              <Note />
            </>
          } />
          <Route path="/CreerRecette" element={<CreerRecette />} />
          <Route path="/ModifierRecette" element={<ModifierRecette />} />
          <Route path="/SupprimeRecette" element={<SupprimeRecette />} />
          <Route path="/PartagerRecette" element={<PartagerRecette />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
